mapper.select:
SELECT u.user_seq, u.email, u.role, u.nickname, u.provider, u.created_at, u.modified_at FROM USER_INFO u
AND u.nickname LIKE CONCAT('%', #{nickname}, '%')
ORDER BY u.created_at DESC LIMIT #{page}, #{size}

SELECT EXISTS(
    SELECT 1 FROM USER_INFO u WHERE u.email = #{email}
)

SELECT EXISTS(
    SELECT 1 FROM USER_INFO u WHERE u.nickname = #{nickname}
)
